function this = fftRectFilt_Plugin(strName)% Author: S. Franz (c) ITAS/IHA @ Jade Hochschule applied licence see EOF% Version History:% Ver. 0.01 initial create 20-Mar-2014 			 SFglobal AlgoCom; this = struct; vars = struct;%% add plugin-properties heresetVar('f_u', 1000, 'numeric', 0, inf);setVar('f_o', 4000, 'numeric', 0, inf);%% add local plugin-variables heref_u_idx = [];f_o_idx = [];%% public and nessesarry functions    function [BlockSettings] = init(BlockSettings)        if ~strcmp(BlockSettings.domain, 'frequency')            pic_message('Plugin expect frequency domain signal', 'error');        end        BlockSettingsIn = BlockSettings;        BlockSettingsOut = BlockSettings;    end    function preprocess()    end    function [out] = process(in)        if getVar('f_u') > getVar('f_o')            pic_message('f_u > f_o');            setVar('f_o', getVar('f_u'));        end        out = in;        out(1 : f_u_idx - 2, :) = 0;        out(f_o_idx + 1: end, :) = 0;        if size(in, 1) == BlockSettingsIn.FFTlen % full Spec            out = out(1 : (end/2 + 1), :);            out = [out ; conj(out(end-1:-1:2,:))];        end    end    function postprocess()    end    function VarUpdated(Varname)        if strcmp(Varname, 'f_u')            f_u_idx = min(floor(getVar('f_u') / BlockSettingsIn.fs * BlockSettingsIn.FFTlen), BlockSettingsIn.FFTlen / 2 + 1);        elseif strcmp(Varname, 'f_o')            f_o_idx = min(floor(getVar('f_o') / BlockSettingsIn.fs * BlockSettingsIn.FFTlen), BlockSettingsIn.FFTlen / 2 + 1);        end    end%--------------------Licence ---------------------------------------------% Copyright (c) <2014> S. Franz% Institut für Technische Assistenzsysteme% Institute for Hearing Technology and Audiology% Jade University of Applied Sciences% Permission is hereby granted, free of charge, to any person obtaining% a copy of this software and associated documentation files% (the "Software")