function this = init_Plugin(strName)% Author: S. Franz (c) ITAS/IHA @ Jade Hochschule applied licence see EOF% Version History:% Ver. 0.01 initial create 20-Mar-2014 			 SFthis = struct; global AlgoCom; vars = struct;this.plugins = struct;setVar('fs', 44100, 'integer', 0, inf);setVar('channels', 2, 'integer', 0, inf);setVar('bands', 1, 'numeric', 0, inf);setVar('blocklen', 1024, 'integer', 0, inf);setVar('FFTlen', 1024, 'integer', 0, inf);%setVar('overlap', .5, 'numeric', 0, inf);setVar('domain', 'time', {'time' 'frequency'});    function [BlockSettings] = init(BlockSettings)        names = fieldnames(vars);        for count = 1 : length(names)            Settings.(char(names(count))) = getVar(char(names(count)));        end        [BlockSettings] = process_subplugs(this.plugins, Settings);    end    function myVars = getVars()        myVars = vars;    endend